package com.sunhydraulics.machine;import org.androidannotations.annotations.AfterViews;import org.androidannotations.annotations.EActivity;import android.app.Activity;import android.os.Handler;@EActivity(R.layout.act_splash)public class SplashActivity extends Activity {	@AfterViews	void init() {		postToMain(2000);	}	Handler mHandler = new Handler();	void postToMain(long delay) {		mHandler.postDelayed(new Runnable() {			@Override			public void run() {				MainActivity_.intent(SplashActivity.this).start();				finish();				overridePendingTransition(android.R.anim.fade_in,						android.R.anim.fade_out);			}		}, delay);	}}