package com.sunhydraulics.machine.adapter;import java.util.List;import android.annotation.SuppressLint;import android.content.Context;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.sunhydraulics.machine.R;import com.sunhydraulics.machine.model.ProductInfo;/** * @author maoweiwei *  */@SuppressLint("InflateParams")public class ProductListAdapter extends AbstractListAdapter<ProductInfo> {	public ProductListAdapter(Context context, List<ProductInfo> content) {		super(context, content);	}	@Override	public View getView(int position, View convertView, ViewGroup parent) {		ViewHolder holder = null;		if (null == convertView) {			convertView = mInflater.inflate(R.layout.layout_product_item_view,					null);			holder = new ViewHolder();			holder.titleView = (TextView) convertView					.findViewById(R.id.itemview);			convertView.setTag(holder);		} else {			holder = (ViewHolder) convertView.getTag();		}		final ProductInfo item = getItem(position);		holder.titleView.setText(item.getName());		return convertView;	}	static class ViewHolder {		TextView titleView;	}}